mapper.select:
select id, corporation_id, notice_title,notice_career, notice_education, notice_end_date, created_date, updated_date, notice_work_start_date, notice_work_end_date, notice_work_start_time, notice_work_end_time, notice_job_category_name from tbl_notice where corporation_id = #{corporationId}
and notice_end_date <= current_date
and notice_end_date > current_date
order by
id notice_end_date
desc limit #{pagination.startRow}, #{pagination.rowCount}

select count(*) from tbl_notice where corporation_id = #{corporationId}
and notice_end_date <= current_date
and notice_end_date > current_date

mapper.update:
update tbl_notice
set notice_read_count = notice_read_count + 1
where id = #{id}
